function dataToTable(thead,cols)
{
    
    var othead = "";
    if(thead){
    othead = "<tr><td colspan='"+(cols.length+2)+"'>"+thead+"</td></tr>";
    }
    
    var otbody = "";
    
    for(var i = 0 ; i < cols.length ;i++){
        
        otbody += "<tr>";
        
        for(var j = 0 ; j < cols[i].length;j++){
            otbody += "<td><p>"+cols[i][j]+"</p></td>";
        }
        
        otbody += "</tr>";
        
    }
    
    var otable =  "<table border='1' cellpadding='0' cellspacing='0' align='center'>"+othead+otbody+"</table>";
    
    return otable;
  
}
function dataTofile(url,title){
    
  var p = "<div class='article-file'><span>附件：</span><br/>";
    
  for(var i = 0 ; i< url.length ;i++){
      
      p+= "<a href='"+url[i]+"' download='"+title[i]+"'>"+title[i]+"</a><br/>";
      
  }
    
    
  p += "</div>"    
  return p ;     
    
}

